    private void ButtonRunActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ButtonRunActionPerformed
       try{
        Constants.ArenaMoveByYears = Integer.parseInt(TextFieldYearToRun.getText());
        /*
         * Call the Forest.run() function when this button is clicked
         */
        SwingWorker s = new SwingWorker<Integer, Void>() {

            @Override
            public Integer doInBackground() {
                ButtonRun.setEnabled(false);
                Forest.run();

                return 0;
            }

            public void done() {
            }
        };
       
        
            s.execute();
        }
        catch(Exception e){
            JOptionPane.showMessageDialog(gui.g, "An Error Occured. Please try again.", "Warning", JOptionPane.WARNING_MESSAGE);
        }

    }//GEN-LAST:event_ButtonRunActionPerformed

