    private void ArenaMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_ArenaMouseClicked
        Point p = evt.getPoint();

        for (Tree t : Forest.arrayOfAliveTrees) {
            if (t != null && t.ContainsPoint(p.x, p.y)) {
                for (Tree t2 : Forest.arrayOfAliveTrees) {
                    if (t2 != null) {
                        t2.isSelected = false;
                    }
                }

                t.isSelected = true;
                LabelID.setText(t.id + "");
                LabelSelectedAge.setText(t.age + "");
                LabelSelectedHeight.setText(((Math.round(t.stemlength * 100)) / 100.0) + "");
                LabelSelectedLeafArea.setText(((Math.round(t.leafarea * 100)) / 100.0) + "");
                LabelSelectedEnergy.setText(Math.round(t.energy * 100) / 100.0 + "");
            }

        }

        for (giraffe t : Forest.arrayOfAliveGiraffes) {
            if (t != null && t.ContainsPoint(p.x, p.y)) {
                for (giraffe t2 : Forest.arrayOfAliveGiraffes) {
                    if (t2 != null) {
                        t2.isSelected = false;
                    }
                }

                t.isSelected = true;
                LabelGID.setText(t.id + "");
                LabelSelectedGAge.setText(t.age + "");
                LabelSelectedGHeight.setText(((Math.round(t.bodylength * 100)) / 100.0) + "");
                LabelSelectedGEnergy.setText(Math.round(t.energy * 100) / 100.0 + "");
                if(t.isHungry)
                    LabelSelectedGStatus.setText("Looking for food");
                if(t.LookingForFemale)
                    LabelSelectedGStatus.setText("Looking for females");
                if(t.isHungry==false&&t.LookingForFemale==false)
                    LabelSelectedGStatus.setText("Loitering");
            }

        }
        
        for (Lion t : Forest.arrayOfAliveLions) {
            if (t != null && t.ContainsPoint(p.x, p.y)) {
                for (Lion t2 : Forest.arrayOfAliveLions) {
                    if (t2 != null) {
                        t2.isSelected = false;
                    }
                }

                t.isSelected = true;
                LabelLID.setText(t.id + "");
                LabelLAge.setText(t.age + "");
                LabelSelectedLSize.setText(((Math.round(t.currentsize * 100)) / 100.0) + "");
                LabelLEnergy.setText(Math.round(t.energy * 100) / 100.0 + "");
            }

        }
        
        


        gui.Arena.repaint();
        gui.Arena.revalidate();


    }//GEN-LAST:event_ArenaMouseClicked

