    private void ButtonGetTreeGenesActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ButtonGetTreeGenesActionPerformed
        for (Tree t : Forest.arrayOfAliveTrees) {
            if (t != null && t.isSelected) {
                g2.LabelID.setText(t.id + "");
                g2.LabelMaxAge.setText(t.dna.maxage + "");
                g2.LabelMaxStemLength.setText(Math.round(t.dna.stemlength * 100) / 100.0 + "");
                g2.LabelMaxLeafArea.setText(Math.round(t.dna.leafarea * 100) / 100.0 + "");
                g2.LabelEnergyToSeed.setText(Math.round(t.dna.energytoseed * 100) / 100.0 + "");
                g2.TextFieldColor.setText("("+t.color.getRed()+","+t.color.getGreen()+","+t.color.getBlue()+")");
                g2.setVisible(true);
            }
        }
        if (g2.isVisible() == false) {
            JOptionPane.showMessageDialog(g, "No Tree selected", "Warning", JOptionPane.WARNING_MESSAGE);
        }

    }//GEN-LAST:event_ButtonGetTreeGenesActionPerformed

    private void ButtonKillPlantActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ButtonKillPlantActionPerformed

        for (Tree t : Forest.arrayOfAliveTrees) {
            if (t != null && t.isSelected) {
                t.kill();
            }
        }
        gui.Arena.repaint();
        gui.Arena.revalidate();
    }//GEN-LAST:event_ButtonKillPlantActionPerformed

