    private void ButtonGetGiraffeGenesActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ButtonGetGiraffeGenesActionPerformed
        for (giraffe t : Forest.arrayOfAliveGiraffes) {
            if (t != null && t.isSelected) {
                g4.LabelGID.setText(t.id + "");
                g4.LabelGMaxAge.setText(t.dna.maxage + "");
                g4.LabelGEnergyToReproduce.setText(Math.round(t.dna.energytoreproduce * 100) / 100.0 + "");
                g4.LabelGMaxSpeed.setText(Math.round(t.dna.maxspeed * 100) / 100.0 + "");
                g4.LabelGRatio.setText(Math.round(t.dna.neckbodyratio * 100) / 100.0 + "");
                g4.LabelGMaxHeight.setText(Math.round(t.dna.height * 100) / 100.0 + "");
                
                
                if (t.gender == false) {
                    g4.ComboGSex.setSelectedIndex(0);
                } else {
                    g4.ComboGSex.setSelectedIndex(1);
                }
                
                if(t.dna.camouflage.fromFemaleParent&&t.dna.camouflage.fromMaleParent)
                    g4.TextFieldCam.setSelectedIndex(2);
                if(t.dna.camouflage.fromFemaleParent==false&&t.dna.camouflage.fromMaleParent==false)
                    g4.TextFieldCam.setSelectedIndex(0);
                if(t.dna.camouflage.fromFemaleParent==true&&t.dna.camouflage.fromMaleParent==false)
                    g4.TextFieldCam.setSelectedIndex(1);
                if(t.dna.camouflage.fromFemaleParent==false&&t.dna.camouflage.fromMaleParent==true)
                    g4.TextFieldCam.setSelectedIndex(1);
                
                g4.setVisible(true);
            }
        }
        if (g4.isVisible() == false) {
            JOptionPane.showMessageDialog(g, "No Giraffe selected", "Warning", JOptionPane.WARNING_MESSAGE);
        }
    }//GEN-LAST:event_ButtonGetGiraffeGenesActionPerformed

