    private void ButtonGetLionGenesActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ButtonGetLionGenesActionPerformed
       for(Lion l:Forest.arrayOfAliveLions){
           if(l!=null&&l.isSelected){
               g8.TextBoxID.setText(l.id+"");
               g8.TextBoxMaxSpeed.setText(Math.round(100*l.currentmaxspeed)/100.0+"");
               g8.TextBoxPreyEfficiency.setText(Math.round(100*l.dna.preyefficiency)/100.0+"");
               g8.TextBoxMaxAge.setText(l.dna.maxage+"");
               g8.TextBoxMaxVision.setText(Math.round(100*l.dna.maxvision)/100.0+"");
               g8.TextBoxEnergyToReproduce.setText(Math.round(100*l.dna.energytoreproduce)/100.0+"");
               g8.TextBoxSize.setText(Math.round(100*l.dna.size)/100.0+"");
               
                
                if (l.gender == false) {
                    g8.ComboBoxGender.setSelectedIndex(0);
                } else {
                    g8.ComboBoxGender.setSelectedIndex(1);
                }
                
                g8.setVisible(true);
               
           }
       }
       
       if (g8.isVisible() == false) {
            JOptionPane.showMessageDialog(g, "No Lion selected", "Warning", JOptionPane.WARNING_MESSAGE);
        }
    }//GEN-LAST:event_ButtonGetLionGenesActionPerformed

